mapper.select:
select * from (select *,ROW_NUMBER() OVER (ORDER BY toweraddtime desc) as rank from( select o.id as id, u.User_name as username, u.User_tele as usertele, u.User_win_type as userwintype, a.area_name as towerareaname, c.City_name as towercityname, t.Tower_name as towername, o.Tower_state as towerstate, o.Tower_fee as towerfee, o.Tower_fact_fee as towerfactfee, CONVERT(varchar, o.Tower_add_time, 120 ) as toweraddtime, s.statename as towerstatename, o.Tower_pj_progress as towerpjprogress, t.ID as towerid, t.Tower_id as towerno, t.Tower_idefined as toweridefined, u.Wx_id as userwxid, uc.City_name as usercityname from tower_order_detail o left join tower_user_info u on u.Wx_id=o.Tower_wx_id left join tower_tower_info t on t.ID=o.Tower_id left join tower_area a on a.ID=t.Tower_area left join tower_city c on c.City_id=a.City_id left join tower_area ua on ua.ID=u.User_area left join tower_city uc on uc.City_id=ua.City_id left join tower_order_state s on s.state=o.Tower_state where 1=1 and t.Tower_is_hb=0
and c.ID in(
#{city.id}
)
and a.ID in(
#{area.id}
)
and u.User_name like '%${parameter.username}%'
and u.User_tele=#{parameter.usertele}
and t.Tower_name like '%${parameter.towername}%'
and t.Tower_id like '%${parameter.towerno}%'
and c.ID=#{parameter.towercity}
and t.Tower_area=#{parameter.towerarea}
and o.Tower_state=#{parameter.towerstate}
and uc.ID=#{parameter.usercity}
and o.Tower_add_time >=#{parameter.toweraddtimestart}
and o.Tower_add_time <= #{parameter.toweraddtimeend}
and o.Tower_state in(
#{state}
)
and o.Tower_state=8
)w1)as tmp where tmp.rank between #{pageno}*#{pageSize}-(#{pageSize}-1) and #{pageno}*#{pageSize}

		select
		count(1)
		from tower_order_detail o
		left join tower_user_info u on
		u.Wx_id=o.Tower_wx_id
		left join tower_tower_info t on t.ID=o.Tower_id
		left join tower_area a on a.ID=t.Tower_area
		left join tower_city c on
		c.City_id=a.City_id
		left join tower_area ua on ua.ID=u.User_area
		left
		join tower_city uc on uc.City_id=ua.City_id
		left join tower_order_state
		s on s.state=o.Tower_state
		where 1=1
		and
		t.Tower_is_hb=0
		
		
			and c.ID in(
			
				#{city.id}
			
			)
		
		
			and a.ID in(
			
				#{area.id}
			
			)
		
		
		
		
			and u.User_name like '%${parameter.username}%'
		
		
