select
count(1)
from tower_order_detail o
left join tower_tower_info t on
t.ID=o.Tower_id
left join tower_area a on a.ID=t.Tower_area
left join
tower_city c on c.City_id=a.City_id
where 1=1
and
t.Tower_is_hb=0


