mapper.select:
select count(1)
from tower_user_order_info
where Wx_id=#{wxid}
;

select o.Order_id as orderid, u.User_name as username, u.User_tele as usertele, case u.User_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end as userlevelname, uc.City_name as usercityname, u.User_win_type as userwintype, u.User_win_time as userwintime, u.User_company as usercompany, v.Type_name as usertype, m.businessname as userbusiness, t.Tower_name as towername, a.area_name as towerareaname, c.City_name as towercityname, t.Tower_idefined as toweridefined, t.Tower_who_yd as towerwhoyd, t.Tower_who_dx as towerwhodx, t.Tower_who_lt as towerwholt, t.Tower_address as toweraddress, case t.Tower_level when 1 then '一级站址' when 2 then '二级站址' when 3 then '三级站址' when 4 then '四级站址' end as towerlevelname, p.Tower_type_name as towertypename, t.Tower_large as towerlarge, t.Tower_j as towerj, t.Tower_w as towerw, t.Tower_manager as towermanager, t.Tower_manager_name as towermanagername, t.Tower_info as towerinfo, o.Tower_project_no as towerprojectno, o.Tower_project_name as towerprojectname, s.statename as towerstatename, o.Tower_fee as towerfee, t.Tower_rent_fee as towerrentfee, o.Tower_fact_fee as towerfactfee, o.Tower_fact_rent_fee as towerfactrentfee, o.Tower_target_address as towertargetaddress, o.Tower_fact_address as towerfactaddress, case o.Tower_power_type when 1 then '直供电' when 2 then '转供电' end as towerpowertype, o.Tower_power_price as towerpowerprice, o.Tower_power_t as towerpowert, o.Tower_yz_name as toweryzname, o.Tower_yz_phone as toweryzphone, CONVERT(varchar, o.Tower_add_time, 120 ) as toweraddtime, ss.storename as towerstorename, ct.name as towercreatetypename
from tower_order_detail o left join tower_user_info u on u.Wx_id=o.Tower_wx_id left join tower_tower_info t on t.ID=o.Tower_id left join tower_area a on a.ID=t.Tower_area left join tower_city c on c.City_id=a.City_id left join tower_city uc on uc.ID=u.User_area left join tower_tower_type p on p.ID=t.Tower_type left join tower_order_state s on s.state=o.Tower_state left join tower_user_type v on v.ID=u.User_type left join tower_user_business m on m.businessid=u.User_business left join tower_tower_store ss on ss.storeid=t.Tower_store left join tower_create_type ct on ct.id=t.Tower_create_type
where 1=1 and t.Tower_is_hb=0
and c.ID in(
#{city.id}
)
and a.ID in(
#{area.id}
)
and u.User_name like '%${parameter.username}%'
and u.User_tele=#{parameter.usertele}
and t.Tower_name like '%${parameter.towername}%'
and c.ID=#{parameter.towercity}
and t.Tower_area=#{parameter.towerarea}
and o.Tower_state=#{parameter.towerstate}
and uc.ID=#{parameter.usercity}
and o.Tower_add_time >=#{parameter.toweraddtimestart}
and o.Tower_add_time <= #{parameter.toweraddtimeend}
and o.Tower_state in(
#{state}
)
and o.Tower_state=8
order by o.Tower_add_time desc

