mapper.select:
select * from (select *,ROW_NUMBER() OVER (ORDER BY addtime desc) as rank from( select a.ID as id, a.Wx_id as wxid, a.User_tele as usertele, a.User_name as username, (case when len(a.User_company)>20 then left (a.User_company,20)+'...' else a.User_company end ) as usercompany, c.City_name as cityname, (case a.user_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end ) as levelname, a.User_type as usertype, d.Type_name as typename, a.User_state as userstate, (case a.User_state when 0 then '冻结' when 1 then '正常' end ) as statename, (case a.User_verify_state when 0 then '未验证' when 1 then '已验证' end ) as verifystatename, (case a.User_team_type when 1 then '个人' when 2 then '企业' end ) as teamtypename, CONVERT(varchar, a.Add_time, 120 ) as addtime, a.User_verify_state as userverifystate, e.businessname as userbusinessname, (case when len(a.User_win_type)>10 then left (a.User_win_type,9)+'...' else a.User_win_type end ) as userwintype, a.User_win_time as userwintime from tower_user_info a LEFT JOIN tower_user_type d on a.User_type = d.ID LEFT join tower_user_business e on e.businessid=a.User_business left join tower_city c on a.User_area = c.ID where 1=1
and c.ID in(
#{city.id}
)
and a.User_tele=#{parameter.usertele}
and a.User_name=#{parameter.username}
and a.User_area=#{parameter.usercity}
and a.user_level=#{parameter.userlevel}
and a.User_state=#{parameter.userstate}
and a.User_type=#{parameter.usertype}
and a.Add_time >=#{parameter.useraddtimestart}
and a.Add_time <= #{parameter.useraddtimeend}
and a.User_bz like '%${parameter.userbz}%'
and a.User_verify_state=#{parameter.userverifystate}
and (a.User_team_img is NULL or a.User_team_img='')
and (a.User_team_img is not NULL and a.User_team_img !='')
) w1)as t where t.rank between #{pageno}*#{pageSize}-(#{pageSize}-1) and #{pageno}*#{pageSize};

		select count(1)
		from tower_user_info a
		LEFT JOIN tower_user_type d on a.User_type = d.ID
		LEFT join tower_user_business e on e.businessid=a.User_business
		left
		join tower_city c on a.User_area = c.ID
		where 1=1
		
		
			and c.ID in(
			
				#{city.id}
			
			)
		
		
		
			and a.User_tele=#{parameter.usertele}
		
		
			and a.User_name=#{parameter.username}
		
		
			and a.User_area=#{parameter.usercity}
		
		
			and a.user_level=#{parameter.userlevel}
		
		
			and a.User_state=#{parameter.userstate}
		
		
			and a.User_type=#{parameter.usertype}
		
		
			and a.Add_time >=#{parameter.useraddtimestart}
		
		
			and a.Add_time  <= 
			#{parameter.useraddtimeend}
		
		
			and a.User_bz like '%${parameter.userbz}%'
		
		
			and a.User_verify_state=#{parameter.userverifystate}
		
		
			
				and (a.User_team_img is NULL or a.User_team_img='')
			
			
				and (a.User_team_img is not NULL and a.User_team_img !='')
			

		
		;

select count(1)
from tower_user_info a
left join tower_city c on a.User_area = c.ID
