select * from (select *,ROW_NUMBER() OVER (ORDER by addtime desc) as rank from( select a.ID as id, a.User_tele as usertele, a.User_name as username, c.City_name as cityname, (case a.user_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end ) as levelname, d.Type_name as typename, e.Charge as charge, e.Get_now as getnow, e.Getting_now as gettingnow, e.All_geting as allgeting, e.Score as score, e.Experience as exp, CONVERT(varchar, a.Add_time, 120 ) as addtime, a.Wx_id as userwxid, a.User_win_type as userwintype, a.User_win_time as userwintime from tower_user_info a LEFT JOIN tower_city c on c.ID =a.User_area LEFT JOIN tower_user_type d on a.User_type = d.ID LEFT JOIN tower_user_charge e on a.Wx_id = e.Wx_id where 1=1
and c.ID in(
#{city.id}
)
and a.User_tele=#{parameter.usertele}
and a.User_name=#{parameter.username}
and a.User_area=#{parameter.usercity}
and a.user_level=#{parameter.userlevel}
and a.User_state=#{parameter.userstate}
and a.User_type=#{parameter.usertype}
and a.Add_time >=#{parameter.useraddtimestart}
and a.Add_time <= #{parameter.useraddtimeend}
) w1)as t where t.rank between #{pageno}*#{pageSize}-(#{pageSize}-1) and #{pageno}*#{pageSize};

select count(1)
from tower_user_info a
LEFT JOIN tower_area b on
a.User_area = b.ID
LEFT JOIN tower_city c on b.City_id = c.City_id
LEFT
JOIN tower_user_type d on a.User_type = d.ID
LEFT JOIN
tower_user_charge e on a.Wx_id = e.Wx_id
where 1=1


	and c.ID in(
	
		#{city.id}
	
	)



	and a.User_tele=#{parameter.usertele}


	and a.User_name=#{parameter.username}


	and a.User_area=#{parameter.usercity}


	and a.user_level=#{parameter.userlevel}


	and a.User_state=#{parameter.userstate}


	and a.User_type=#{parameter.usertype}


	and a.Add_time >=#{parameter.useraddtimestart}


	and a.Add_time  <= 
	#{parameter.useraddtimeend}

;

mapper.update:
update
tower_user_info
set

	User_state = #{state}


	User_verify_state = #{verifystate}

where wx_id = #{wxid};

update tower_user_info
set User_type = #{usertype}
where wx_id = #{wxid};

update tower_user_info
set User_bz = #{userbz}
where wx_id = #{wxid};

mapper.select:
select a.ID as id, a.Wx_id as wxid, a.Wx_name as wxname, a.User_tele as usertele, case a.User_sex when 1 then '男' when 2 then '女' end as usersex, a.User_name as username, a.User_company as usercompany, s.businessname as userbusiness, c.City_name as cityname, (case a.user_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end ) as levelname, a.User_type as usertype, d.Type_name as typename, a.User_state as userstate, (case a.User_state when 0 then '冻结' when 1 then '正常' end ) as statename, (case a.User_verify_state when 0 then '未验证' when 1 then '已验证' end ) as verifystatename, (case a.User_team_type when 1 then '个人' when 2 then '企业' end ) as teamtypename, a.Add_time as addtime, case when a.User_team_img is NULL or a.User_team_img='' then '未上传' else '已上传' end as userteamimg, a.User_win_type as userwintype, a.User_win_time as userwintime from tower_user_info a LEFT JOIN tower_city c on c.ID = a.User_area LEFT JOIN tower_user_type d on a.User_type = d.ID left join tower_user_business s on s.businessid=a.User_business
where 1=1
and c.ID in(
#{city.id}
)
and a.User_tele=#{parameter.usertele}
and a.User_name=#{parameter.username}
and a.User_area=#{parameter.usercity}
and a.user_level=#{parameter.userlevel}
and a.User_state=#{parameter.userstate}
and a.User_type=#{parameter.usertype}
and a.Add_time >=#{parameter.useraddtimestart}
and a.Add_time <= #{parameter.useraddtimeend}
and a.User_bz like '%${parameter.userbz}%'
and a.User_verify_state=#{parameter.userverifystate}
order by a.Add_time desc ;

		select
		a.User_tele as usertele,
		a.User_name as username,
		a.User_company as usercompany,
		a.User_team_img as userteamimage
		from tower_user_info a
		LEFT JOIN
		tower_city c on c.ID = a.User_area
		LEFT JOIN tower_user_type d on
		a.User_type = d.ID
		left join tower_user_business s on
		s.businessid=a.User_business
		where 1=1
		
		
			and c.ID in(
			
				#{city.id}
			
			)
		
		
		
			and a.User_tele=#{parameter.usertele}
		
		
			and a.User_name=#{parameter.username}
		
		
			and a.User_area=#{parameter.usercity}
		
		
			and a.user_level=#{parameter.userlevel}
		
		
			and a.User_state=#{parameter.userstate}
		
		
			and a.User_type=#{parameter.usertype}
		
		
			and a.Add_time >=#{parameter.useraddtimestart}
		
		
			and a.Add_time  <= 
			#{parameter.useraddtimeend}
		
		
			and a.User_bz like '%${parameter.userbz}%'
		
		
			and a.User_verify_state=#{parameter.userverifystate}
		
		order by a.Add_time desc
		;

select a.ID as id, a.User_name as username, a.User_tele as usertele, c.City_name as cityname, (case a.user_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end ) as levelname, d.Type_name as typename, e.Charge as charge, e.Get_now as getnow, e.Getting_now as gettingnow, e.All_geting as allgeting, e.Score as score, e.Experience as exp, a.Add_time as addtime from tower_user_info a LEFT JOIN tower_city c on c.ID = a.User_area LEFT JOIN tower_user_type d on a.User_type = d.ID LEFT JOIN tower_user_charge e on a.Wx_id = e.Wx_id where 1=1
and c.ID in(
#{city.id}
)
and a.User_tele=#{parameter.usertele}
and a.User_name=#{parameter.username}
and a.User_area=#{parameter.usercity}
and a.user_level=#{parameter.userlevel}
and a.User_state=#{parameter.userstate}
and a.User_type=#{parameter.usertype}
and a.Add_time >=#{parameter.useraddtimestart}
and a.Add_time <= #{parameter.useraddtimeend}

