mapper.select:
		select
		u.Wx_id as wxid
		from tower_user_info u
		left join tower_city c on
		u.User_area=c.ID
		where 1=1
		
			and u.User_level=#{userlevel}
		
		
			and u.User_type=#{usertype}
		
		
			and u.User_area in(
			
				#{city}
			
			)

select s.source,i.info, ( select COUNT(1) from tower_user_info u LEFT JOIN tower_city c on c.ID = u.User_area where 1=1 and u.User_source=i.infoid
and c.ID in(
#{city.id}
)
)as count from tower_user_source_info i left join tower_user_source s on s.sourceid=i.sourceid
where 1=1
and s.sourceid=#{sourceid}
and i.infoid=#{infoid}

select s.source,i.info, ( select COUNT(1) from tower_user_info u LEFT JOIN tower_city c on c.ID = u.User_area where 1=1 and u.User_source=i.infoid
and c.ID in(
#{city.id}
)
)as count from tower_user_source_info i left join tower_user_source s on s.sourceid=i.sourceid
where 1=1
and s.sourceid=#{sourceid}
and i.infoid=#{infoid}
